import { Box } from '@chakra-ui/react'
import styled from 'styled-components'

export const NinjaLogo = styled.img`
    height: 50px;
    width: 50px;
    margin-right: auto;
`

export const Title = styled.p`
    font-size: 2rem;
    font-weight: bold;
`

export const StyledChakraBox = styled(Box)`
    display: flex;
    justify-content: center;
`
