import React, { Component } from "react";
import { Button, Input, Select } from "@chakra-ui/react";
import { StyledForm } from "./CreateJobs.styles";
import { purple } from "../../colors/colors";

// //api key: {
//     "key": "5d01f6fa-a9ef-420d-b0d4-46d1ba31c6bc"
//   }

// - Toda proposta de serviço deve conter, no mínimo, os seguintes campos:
//     - Título
//     - Descrição
//     - Valor da remuneração
//     - Método(s) de pagamento oferecidos
//     - Prazo

export default class CreateJobs extends Component {
  state = {
    title: "",
    description: "",
    price: "",
    paymentMethod: [],
    date: "",
  };

  onChangeTitle = (event) => {
    this.setState({
      title: event.target.value,
    });
  };

  onChangeDescription = (event) => {
    this.setState({
      description: event.target.value,
    });
  };

  onChangePrice = (event) => {
    this.setState({
      price: event.target.value,
    });
  };

  onChangePaymentMethod = (event) => {
    const newPaymentMethods = [...this.state.paymentMethod];
    newPaymentMethods.push(event.target.value);

    this.setState({
      paymentMethod: newPaymentMethods,
    });
  };

  onChangeDate = (event) => {
    this.setState({
      date: event.target.value,
    });
  };

  render() {
    console.log(this.state);
    return (
      <div>
        <StyledForm spacing={3}>
          <Input
            onChange={this.onChangeTitle}
            value={this.state.title}
            isRequired
            placeholder="Título*"
            type={"text"}
          />
          <Input
            onChange={this.onChangeDescription}
            value={this.state.description}
            isRequired
            placeholder="Descrição*"
            type={"text"}
          />
          <Input
            onChange={this.onChangePrice}
            value={this.state.price}
            isRequired
            placeholder="R$"
            type={"number"}
          />
          <Select
            onChange={this.onChangePaymentMethod}
            isRequired
            placeholder="Select option"
            multiple
          >
            <option value="Cartão de Crédito">Cartão de Crédito</option>
            <option value="Cartão de Débito">Cartão de Débito</option>
            <option value="Pix">Pix</option>
            <option value="PayPal">PayPal</option>
            <option value="Boleto">Boleto</option>
          </Select>
          <Input
            onChange={this.onChangeDate}
            value={this.state.date}
            isRequired
            placeholder="dd/mm/yyy"
            type={"date"}
          />
          <Button bg={purple}>Enviar</Button>
        </StyledForm>
      </div>
    );
  }
}
