import React, { Component } from 'react'
import { JobCard, Title, CartIcon } from './Card.styles'
import cart from '../../assets/add-to-cart.png'
import { Button } from '@chakra-ui/react'
import { purple } from '../../colors/colors'
export default class Card extends Component {

  render() {
    console.log(this.props)
    return (
      <JobCard direction="column">
        <Title>{this.props.job.title}</Title>
        <p>{this.props.job.description}</p>
        <p>{this.props.job.dueDate}</p>
        <p>{this.props.job.price}</p>
        <div><Button bg={purple} variant={'outline'}>Ver detalhes</Button>
        <CartIcon src={cart} alt="carrinho" /></div>

      </JobCard>
    )
  }
}
