import React from "react";
import { Button, ChakraProvider } from "@chakra-ui/react";
import CreateJobs from "./components/CreateJobs/CreateJobs";
import HomePage from "./components/Homepage/HomePage";
import JobsList from "./components/JobsList/JobsList";
import { purple } from "./colors/colors";
import ninjaLogo from "./assets/ninja.png";
import {
  NinjaLogo,
  Title,
  StyledChakraBox,
  MainPage,
  NinjaBackground,
  NinjaBackgroundLogo,
} from "./app.styles";
// - Deve haver duas áreas separadas no site, uma destinada à criação dos serviços oferecidos e outra destinada à listagem e busca dessas ofertas.
// - Na parte de busca e listagem o usuário deve ser capaz de ver mais detalhes de uma oferta e decidir se deseja contratá-la (também será possível que o usuário desista da contratação).
// - Deverá haver um carrinho onde o usuário possa adicionar propostas para contratá-las.
// - Toda proposta de serviço deve conter, no mínimo, os seguintes campos:
//     - Título
//     - Descrição
//     - Valor da remuneração
//     - Método(s) de pagamento oferecidos
//     - Prazo
// - Na listagem de trabalhos o usuário deve ser capaz:
//     - De filtrar por:
//         - Valor mínimo e máximo
//         - Título ou descrição (busca)
//     - De ordenar por:
//         - Título
//         - Valor da remuneração
//         - Prazo

export default class App extends React.Component {
  state = {
    currentPage: "",
  };

  renderPage = () => {
    switch (this.state.currentPage) {
      case "createJobs":
        return <CreateJobs />;
      case "jobsList":
        return <JobsList />;
      default:
        return <HomePage />;
    }
  };
  render() {
    return (
      <ChakraProvider>
        <MainPage>
          <StyledChakraBox bg={purple} w="100%" p={2} color="white">
            <NinjaLogo src={ninjaLogo} alt={"ninja logo"} />
            <Title>Labeninjas</Title>
          </StyledChakraBox>
          <NinjaBackground>
            <NinjaBackgroundLogo src={ninjaLogo} alt={"ninja logo"} />
            <div>
              <Button onClick={this.changePage} bg={purple}>Quero ser um ninja</Button>
              <Button m={2} bg={purple}>
                Contratar um ninja
              </Button>
            </div>
          </NinjaBackground>
        </MainPage>
      </ChakraProvider>
    );
  }
}
